var formatNumber = {
    separador: ".", 
    sepDecimal: ',', 
    formatear:function (num){
    num +='';
    var splitStr = num.split('.');
    var splitLeft = splitStr[0];
    var splitRight = splitStr.length > 1 ? this.sepDecimal + splitStr[1] : '';
    var regx = /(\d+)(\d{3})/;
    while (regx.test(splitLeft)) {
    splitLeft = splitLeft.replace(regx, '$1' + this.separador + '$2');
    }
    return this.simbol + splitLeft +splitRight;
    },
    new:function(num, simbol){
    this.simbol = simbol ||'';
    return this.formatear(num);
    }
}

//Encargado de calcular valor segun recargos
function CalculateValueUC(taxo, uc){
    console.log(taxo.toString());
    var taxN = taxo.replace( /^\D+/g, '');

    switch(taxN){
        case "1":
        case "2":
        case "3":
        case "4":
        case "5":
        case "6":
        //sin recargo
            console.log("sin recargo");

            return formatNumber.new(uc * ValueUC);
        break;

        case "7":
        case "8":
        case "9":
        //+ 30%
            console.log("+ 30%");
            var a = uc * 1.3;
            return formatNumber.new(Math.round(a * ValueUC));

        break;
        
        default:
            console.log("error");
        break;
    }
}

